import { useState } from "react";

// @mui material components
import Modal from "@mui/material/Modal";
import Box from "@mui/material/Box";
import Icon from "@mui/material/Icon";

// Argon Dashboard 2 MUI components
import ArgonBox from "components/ArgonBox";
import ArgonTypography from "components/ArgonTypography";
import ArgonButton from "components/ArgonButton";
import LeafletMap from "components/LeafletMap";

export function MapModal({ isOpen, onClose, location, hospitalName }) {
  // Convert location string to array of numbers
  const locationCoords = location.split(',').map(coord => parseFloat(coord.trim()));

  return (
    <Modal
      open={isOpen}
      onClose={onClose}
      aria-labelledby="map-modal-title"
      aria-describedby="map-modal-description"
    >
      <Box sx={{
        position: 'absolute',
        top: '50%',
        left: '50%',
        transform: 'translate(-50%, -50%)',
        width: '80vw',
        maxWidth: '800px',
        height: '70vh',
        bgcolor: 'background.paper',
        borderRadius: '10px',
        boxShadow: '0 8px 32px 0 rgba(0,0,0,0.1)',
        p: 0,
        overflow: 'hidden'
      }}>
        <ArgonBox 
          p={2} 
          borderBottom="1px solid" 
          borderColor="rgb(226, 232, 240)"
          display="flex"
          justifyContent="space-between"
          alignItems="center"
        >
          <ArgonTypography variant="h6" fontWeight="medium">
            {hospitalName} Location
          </ArgonTypography>
          <ArgonButton 
            variant="text" 
            color="primary"
            onClick={onClose}
            sx={{ minWidth: 'auto', p: 1 }}
          >
            <Icon sx={{ color: 'rgb(103, 116, 142)' }}>close</Icon>
          </ArgonButton>
        </ArgonBox>
        <Box sx={{ width: '100%', height: 'calc(100% - 60px)' }}>
          <LeafletMap 
            location={locationCoords} 
            name={hospitalName} 
            style={{ height: '100%', width: '100%' }} 
          />
        </Box>
      </Box>
    </Modal>
  );
}

export function LocationButton({ location, hospitalName }) {
  const [isModalOpen, setIsModalOpen] = useState(false);

  return (
    <>
      <ArgonButton
        variant="text"
        color="primary"
        onClick={() => setIsModalOpen(true)}
        sx={{ minWidth: 'auto' }}
      >
        <Icon sx={{ color: 'rgb(103, 116, 142)' }}>location_on</Icon>
      </ArgonButton>
      <MapModal
        isOpen={isModalOpen}
        onClose={() => setIsModalOpen(false)}
        location={location}
        hospitalName={hospitalName}
      />
    </>
  );
}
