/* eslint-disable react/prop-types */
// @mui material components
import Icon from "@mui/material/Icon";
import IconButton from "@mui/material/IconButton";
import LocationOnIcon from "@mui/icons-material/LocationOn";
import Modal from "@mui/material/Modal";
import Box from "@mui/material/Box";
import MoreVertIcon from "@mui/icons-material/MoreVert";

import { MapContainer, TileLayer, Marker, Popup } from 'react-leaflet';
import 'leaflet/dist/leaflet.css';

// Argon Dashboard 2 MUI components
import ArgonBox from "components/ArgonBox";
import ArgonTypography from "components/ArgonTypography";
import ArgonAvatar from "components/ArgonAvatar";
import ArgonBadge from "components/ArgonBadge";

// React imports
import { useState } from "react";

// Images
import school1 from "assets/images/school-1.png";
import school2 from "assets/images/school-2.png";
import school3 from "assets/images/school-3.jpg";

function School({ image, name }) {
  return (
    <ArgonBox display="flex" alignItems="center">
      <ArgonBox mr={2}>
        <ArgonAvatar src={image} alt={name} size="sm" variant="rounded" />
      </ArgonBox>
      <ArgonTypography variant="button" fontWeight="medium">
        {name}
      </ArgonTypography>
    </ArgonBox>
  );
}

function MapModal({ isOpen, onClose, location, schoolName }) {
  const [latitude, longitude] = location.split(',').map(Number);

  return (
    <Modal
      open={isOpen}
      onClose={onClose}
      aria-labelledby="map-modal-title"
    >
      <Box sx={{
        position: 'absolute',
        top: '50%',
        left: '50%',
        transform: 'translate(-50%, -50%)',
        width: 600,
        height: 450,
        bgcolor: 'background.paper',
        borderRadius: '10px',
        boxShadow: 24,
        p: 4,
      }}>
        <ArgonTypography variant="h6" fontWeight="medium" mb={2}>
          {schoolName} Location
        </ArgonTypography>
        <Box sx={{ flexGrow: 1, height: '100%' }}>
          <MapContainer center={[latitude, longitude]} zoom={13} style={{ height: "100%", width: "100%" }}>
            <TileLayer
              url="https://{s}.tile.openstreetmap.org/{z}/{x}/{y}.png"
              attribution='&copy; <a href="https://www.openstreetmap.org/copyright">OpenStreetMap</a>'
            />
            <Marker position={[latitude, longitude]}>
              <Popup>{schoolName}</Popup>
            </Marker>
          </MapContainer>
        </Box>
      </Box>
    </Modal>
  );
}

function LocationButton({ location, schoolName }) {
  const [isModalOpen, setIsModalOpen] = useState(false);

  return (
    <>
      <IconButton onClick={() => setIsModalOpen(true)}>
        <LocationOnIcon />
      </IconButton>
      <MapModal
        isOpen={isModalOpen}
        onClose={() => setIsModalOpen(false)}
        location={location}
        schoolName={schoolName}
      />
    </>
  );
}

const authorsTableData = {
  columns: [
    { name: "school", align: "left", label: "SCHOOL" },
    { name: "type", align: "left", label: "TYPE" },
    { name: "lga", align: "left", label: "LGA" },
    { name: "license status", align: "center", label: "LICENSE STATUS" },
    { name: "number of staff", align: "center", label: "NUMBER OF STAFF" },
    { name: "expected income from paye", align: "right", label: "EXPECTED INCOME FROM PAYE" },
    { name: "paye tax status", align: "center", label: "PAYE STATUS" },
    { name: "action", align: "center", label: "ACTION" },
    { name: "location", align: "center", label: "LOCATION" },
  ],

  rows: [
    {
      school: <School image={school1} name="Government Secondary School Jos" />,
      type: (
        <ArgonTypography variant="caption" color="text" fontWeight="medium" type="public">
          Public
        </ArgonTypography>
      ),
      lga: (
        <ArgonTypography variant="caption" color="text" fontWeight="medium">
          Jos North
        </ArgonTypography>
      ),
      "license status": (
        <ArgonBadge variant="gradient" badgeContent="RENEWED" color="success" size="xs" container />
      ),
      "number of staff": (
        <ArgonTypography variant="caption" color="text" fontWeight="medium">
          150
        </ArgonTypography>
      ),
      "expected income from paye": (
        <ArgonTypography variant="caption" color="text" fontWeight="medium">
          150,000
        </ArgonTypography>
      ),
      "paye tax status": (
        <ArgonBadge variant="gradient" badgeContent="PAID" color="success" size="xs" container />
      ),
      action: (
        <IconButton>
          <MoreVertIcon />
        </IconButton>
      ),
      location: <LocationButton location="9.8965,8.8583" schoolName="Government Secondary School Jos" />,
    },
    {
      school: <School image={school2} name="St. Louis College Jos" />,
      type: (
        <ArgonTypography variant="caption" color="text" fontWeight="medium" type="private">
          Private
        </ArgonTypography>
      ),
      lga: (
        <ArgonTypography variant="caption" color="text" fontWeight="medium">
          Jos South
        </ArgonTypography>
      ),
      "license status": (
        <ArgonBadge variant="gradient" badgeContent="EXPIRED" color="error" size="xs" container />
      ),
      "number of staff": (
        <ArgonTypography variant="caption" color="text" fontWeight="medium">
          200
        </ArgonTypography>
      ),
      "expected income from paye": (
        <ArgonTypography variant="caption" color="text" fontWeight="medium">
          200,000
        </ArgonTypography>
      ),
      "paye tax status": (
        <ArgonBadge variant="gradient" badgeContent="UNPAID" color="error" size="xs" container />
      ),
      action: (
        <IconButton>
          <MoreVertIcon />
        </IconButton>
      ),
      location: <LocationButton location="9.8420,8.8722" schoolName="St. Louis College Jos" />,
    },
    {
      school: <School image={school3} name="Federal Government College Jos" />,
      type: (
        <ArgonTypography variant="caption" color="text" fontWeight="medium" type="public">
          Public
        </ArgonTypography>
      ),
      lga: (
        <ArgonTypography variant="caption" color="text" fontWeight="medium">
          Jos North
        </ArgonTypography>
      ),
      "license status": (
        <ArgonBadge variant="gradient" badgeContent="RENEWED" color="success" size="xs" container />
      ),
      "number of staff": (
        <ArgonTypography variant="caption" color="text" fontWeight="medium">
          300
        </ArgonTypography>
      ),
      "expected income from paye": (
        <ArgonTypography variant="caption" color="text" fontWeight="medium">
          300,000
        </ArgonTypography>
      ),
      "paye tax status": (
        <ArgonBadge variant="gradient" badgeContent="PAID" color="success" size="xs" container />
      ),
      action: (
        <IconButton>
          <MoreVertIcon />
        </IconButton>
      ),
      location: <LocationButton location="9.8965,8.8583" schoolName="Federal Government College Jos" />,
    },
  ],
};

export default authorsTableData;
