import { useState, useMemo } from "react";

// @mui material components
import Grid from "@mui/material/Grid";
import Card from "@mui/material/Card";
import Icon from "@mui/material/Icon";
import Menu from "@mui/material/Menu";
import MenuItem from "@mui/material/MenuItem";
import TextField from "@mui/material/TextField";
import InputAdornment from "@mui/material/InputAdornment";
import SearchIcon from "@mui/icons-material/Search";
import Button from "@mui/material/Button";
import FilterListIcon from "@mui/icons-material/FilterList";
import Tabs from "@mui/material/Tabs";
import Tab from "@mui/material/Tab";

// Argon Dashboard 2 MUI components
import ArgonBox from "components/ArgonBox";
import ArgonTypography from "components/ArgonTypography";

// Argon Dashboard 2 MUI examples
import DashboardLayout from "examples/LayoutContainers/DashboardLayout";
import DashboardNavbar from "examples/Navbars/DashboardNavbar";
import Footer from "examples/Footer";
import Table from "examples/Tables/Table";

// Data
import hotelsTableData from "./data/projectsTableData";

function Hotels() {
  const { columns, rows } = hotelsTableData;
  const [tabValue, setTabValue] = useState(0);
  const [searchQuery, setSearchQuery] = useState("");

  // Filter states and anchors
  const [licenseAnchor, setLicenseAnchor] = useState(null);
  const [roomsAnchor, setRoomsAnchor] = useState(null);
  const [payeAnchor, setPayeAnchor] = useState(null);

  const [licenseFilter, setLicenseFilter] = useState("all");
  const [roomsFilter, setRoomsFilter] = useState("all");
  const [payeFilter, setPayeFilter] = useState("all");

  // Stats
  const totalHotels = rows.length;
  const totalRooms = rows.reduce((sum, row) => {
    try {
      const roomsText = row["number of rooms"]?.props?.children || '0';
      return sum + parseInt(roomsText);
    } catch (error) {
      console.error('Error calculating rooms:', error);
      return sum;
    }
  }, 0);
  const totalIncome = rows.reduce((sum, row) => {
    try {
      const incomeText = row["expected income"]?.props?.children || '0';
      return sum + parseInt(incomeText.replace(/,/g, ''));
    } catch (error) {
      console.error('Error calculating income:', error);
      return sum;
    }
  }, 0);
  const paidCount = rows.filter(row => {
    try {
      return row["paye tax status"]?.props?.badgeContent === "PAID";
    } catch (error) {
      console.error('Error checking paid status:', error);
      return false;
    }
  }).length;

  const standardHotels = rows.filter(row => {
    try {
      return row.type?.props?.badgeContent === "STANDARD";
    } catch (error) {
      console.error('Error filtering standard hotels:', error);
      return false;
    }
  });
  const luxuryHotels = rows.filter(row => {
    try {
      return row.type?.props?.badgeContent === "LUXURY";
    } catch (error) {
      console.error('Error filtering luxury hotels:', error);
      return false;
    }
  });
  const budgetHotels = rows.filter(row => {
    try {
      return row.type?.props?.badgeContent === "BUDGET";
    } catch (error) {
      console.error('Error filtering budget hotels:', error);
      return false;
    }
  });

  const handleTabChange = (event, newValue) => {
    setTabValue(newValue);
  };

  // Filter handlers
  const handleFilterClick = (event, setAnchor) => {
    setAnchor(event.currentTarget);
  };

  const handleFilterClose = (setAnchor) => {
    setAnchor(null);
  };

  const handleFilterSelect = (filter, value, setFilter, setAnchor) => {
    setFilter(value);
    setAnchor(null);
  };

  // Get unique values for filters
  const roomSizeRanges = ["0-50", "51-100", "101-200", "200+"];

  const filteredRows = useMemo(() => {
    let filtered = rows;

    // Filter by search query
    if (searchQuery) {
      filtered = filtered.filter(row => {
        try {
          const hotelName = row.hotel[1].toLowerCase();
          return hotelName.includes(searchQuery.toLowerCase());
        } catch (error) {
          console.error('Error filtering by search query:', error);
          return true;
        }
      });
    }

    // Filter by tab
    if (tabValue === 0) {
      filtered = filtered.filter(row => {
        try {
          return row.type?.props?.badgeContent === "STANDARD";
        } catch (error) {
          console.error('Error filtering standard hotels:', error);
          return false;
        }
      });
    } else if (tabValue === 1) {
      filtered = filtered.filter(row => {
        try {
          return row.type?.props?.badgeContent === "LUXURY";
        } catch (error) {
          console.error('Error filtering luxury hotels:', error);
          return false;
        }
      });
    } else if (tabValue === 2) {
      filtered = filtered.filter(row => {
        try {
          return row.type?.props?.badgeContent === "BUDGET";
        } catch (error) {
          console.error('Error filtering budget hotels:', error);
          return false;
        }
      });
    }

    // Apply license filter
    if (licenseFilter !== "all") {
      filtered = filtered.filter(row => {
        try {
          return row["license status"]?.props?.badgeContent === licenseFilter;
        } catch (error) {
          console.error('Error filtering by license status:', error);
          return false;
        }
      });
    }

    // Apply rooms filter
    if (roomsFilter !== "all") {
      filtered = filtered.filter(row => {
        try {
          const rooms = parseInt(row["number of rooms"]?.props?.children || '0');
          switch (roomsFilter) {
            case "0-50": return rooms <= 50;
            case "51-100": return rooms > 50 && rooms <= 100;
            case "101-200": return rooms > 100 && rooms <= 200;
            case "200+": return rooms > 200;
            default: return true;
          }
        } catch (error) {
          console.error('Error filtering by rooms:', error);
          return false;
        }
      });
    }

    // Apply PAYE filter
    if (payeFilter !== "all") {
      filtered = filtered.filter(row => {
        try {
          return row["paye tax status"]?.props?.badgeContent === payeFilter;
        } catch (error) {
          console.error('Error filtering by PAYE status:', error);
          return false;
        }
      });
    }

    return filtered;
  }, [rows, searchQuery, tabValue, licenseFilter, roomsFilter, payeFilter]);

  return (
    <DashboardLayout>
      <DashboardNavbar />
      <ArgonBox py={3}>
        <Grid container spacing={3} mb={3}>
          <Grid item xs={12}>
            <Card>
              <ArgonBox p={3}>
                <Grid container spacing={3}>
                  <Grid item xs={12} md={6}>
                    <ArgonTypography variant="h4" fontWeight="medium">
                      Hotels Dashboard
                    </ArgonTypography>
                    <ArgonBox display="flex" gap={2} mt={1}>
                      <ArgonTypography variant="button" fontWeight="regular" color="text">
                        {totalHotels} hotels
                      </ArgonTypography>
                      <ArgonTypography variant="button" fontWeight="regular" color="text">
                        •
                      </ArgonTypography>
                      <ArgonTypography variant="button" fontWeight="regular" color="text">
                        {totalRooms} rooms
                      </ArgonTypography>
                      <ArgonTypography variant="button" fontWeight="regular" color="text">
                        •
                      </ArgonTypography>
                      <ArgonTypography variant="button" fontWeight="regular" color="text">
                        ₦{totalIncome.toLocaleString()} Income
                      </ArgonTypography>
                      <ArgonTypography variant="button" fontWeight="regular" color="text">
                        •
                      </ArgonTypography>
                      <ArgonTypography variant="button" fontWeight="regular" color="text">
                        {paidCount} paid
                      </ArgonTypography>
                    </ArgonBox>
                  </Grid>
                  <Grid item xs={12} md={6} display="flex" justifyContent="flex-end">
                    <TextField
                      placeholder="Search hotels..."
                      variant="outlined"
                      size="small"
                      value={searchQuery}
                      onChange={(e) => setSearchQuery(e.target.value)}
                      InputProps={{
                        startAdornment: (
                          <InputAdornment position="start">
                            <SearchIcon />
                          </InputAdornment>
                        ),
                      }}
                      sx={{ width: 300 }}
                    />
                  </Grid>
                </Grid>
              </ArgonBox>
            </Card>
          </Grid>
        </Grid>

        <Card>
          <ArgonBox p={3}>
            <Tabs
              value={tabValue}
              onChange={handleTabChange}
              textColor="primary"
              indicatorColor="primary"
            >
              <Tab label={`Standard Hotels (${standardHotels.length})`} />
              <Tab label={`Luxury Hotels (${luxuryHotels.length})`} />
              <Tab label={`Budget Hotels (${budgetHotels.length})`} />
            </Tabs>
          </ArgonBox>

          <ArgonBox>
            <Table columns={columns} rows={filteredRows} />
          </ArgonBox>
        </Card>

        <Footer />
      </ArgonBox>
    </DashboardLayout>
  );
}

export default Hotels;
