/**
=========================================================
* Argon Dashboard 2 MUI - v3.0.1
=========================================================
*/

import React, { useState } from "react";
// @mui material components
import Card from "@mui/material/Card";
import TextField from "@mui/material/TextField";
import InputAdornment from "@mui/material/InputAdornment";
import SearchIcon from "@mui/icons-material/Search";
import Tabs from "@mui/material/Tabs";
import Tab from "@mui/material/Tab";
import Select from "@mui/material/Select";
import MenuItem from "@mui/material/MenuItem";
import FormControl from "@mui/material/FormControl";
import InputLabel from "@mui/material/InputLabel";
import Grid from "@mui/material/Grid";
import IconButton from "@mui/material/IconButton";
import SortIcon from "@mui/icons-material/Sort";
import Tooltip from "@mui/material/Tooltip";

// Argon Dashboard 2 MUI components
import ArgonBox from "components/ArgonBox";
import ArgonTypography from "components/ArgonTypography";

// Argon Dashboard 2 MUI examples
import DashboardLayout from "examples/LayoutContainers/DashboardLayout";
import DashboardNavbar from "examples/Navbars/DashboardNavbar";
import Footer from "examples/Footer";
import Table from "examples/Tables/Table";

// Data
import hospitalsTableData from "./data/projectsTableData";

function Hospitals() {
  const { columns, rows } = hospitalsTableData;
  const [tabValue, setTabValue] = useState(0);
  const [searchQuery, setSearchQuery] = useState("");
  const [licenseFilter, setLicenseFilter] = useState("all");
  const [lgaFilter, setLgaFilter] = useState("all");
  const [staffFilter, setStaffFilter] = useState("all");
  const [payeFilter, setPayeFilter] = useState("all");
  const [sortConfig, setSortConfig] = useState({ key: null, direction: 'asc' });

  const handleTabChange = (event, newValue) => {
    setTabValue(newValue);
  };

  const handleSearchChange = (event) => {
    setSearchQuery(event.target.value.toLowerCase());
  };

  const handleLicenseFilterChange = (event) => {
    setLicenseFilter(event.target.value);
  };

  const handleLgaFilterChange = (event) => {
    setLgaFilter(event.target.value);
  };

  const handleStaffFilterChange = (event) => {
    setStaffFilter(event.target.value);
  };

  const handlePayeFilterChange = (event) => {
    setPayeFilter(event.target.value);
  };

  const handleSort = (key) => {
    setSortConfig(prevConfig => ({
      key,
      direction: prevConfig.key === key && prevConfig.direction === 'asc' ? 'desc' : 'asc'
    }));
  };

  // Get unique LGAs for the filter
  const uniqueLgas = [...new Set(rows.map(row => 
    row.lga?.props?.children?.toString() || ""
  ))].filter(Boolean).sort();

  let filteredRows = rows.filter((row) => {
    // Hospital type filter (tabs)
    const typeMatch = tabValue === 0 ? 
      row.type?.props?.type === "public" : 
      row.type?.props?.type === "private";

    // Search filter across all fields
    const searchMatch = Object.entries(row).some(([key, value]) => {
      if (Array.isArray(value)) {
        return value[1]?.toString().toLowerCase().includes(searchQuery);
      }
      return value?.props?.children?.toString().toLowerCase().includes(searchQuery) ||
             value?.props?.badgeContent?.toString().toLowerCase().includes(searchQuery);
    });

    // License status filter
    const licenseMatch = licenseFilter === "all" || 
      row["license status"]?.props?.badgeContent === licenseFilter;

    // LGA filter
    const lgaMatch = lgaFilter === "all" || 
      row.lga?.props?.children === lgaFilter;

    // Staff size filter
    let staffMatch = true;
    if (staffFilter !== "all") {
      const staffCount = parseInt(row["number of staff"]?.props?.children);
      switch (staffFilter) {
        case "small":
          staffMatch = staffCount < 100;
          break;
        case "medium":
          staffMatch = staffCount >= 100 && staffCount < 200;
          break;
        case "large":
          staffMatch = staffCount >= 200;
          break;
      }
    }

    // PAYE status filter
    const payeMatch = payeFilter === "all" || 
      row["paye tax status"]?.props?.badgeContent === payeFilter;

    return typeMatch && searchMatch && licenseMatch && lgaMatch && staffMatch && payeMatch;
  });

  // Apply sorting if configured
  if (sortConfig.key) {
    filteredRows.sort((a, b) => {
      let aValue = a[sortConfig.key]?.props?.children;
      let bValue = b[sortConfig.key]?.props?.children;

      // Handle array case (e.g., hospital name)
      if (Array.isArray(a[sortConfig.key])) {
        aValue = a[sortConfig.key][1];
        bValue = b[sortConfig.key][1];
      }

      // Handle numeric values
      if (!isNaN(aValue)) {
        aValue = Number(aValue);
        bValue = Number(bValue);
      }

      if (aValue < bValue) return sortConfig.direction === 'asc' ? -1 : 1;
      if (aValue > bValue) return sortConfig.direction === 'asc' ? 1 : -1;
      return 0;
    });
  }

  // Calculate summary statistics
  const totalStaff = filteredRows.reduce((sum, row) => 
    sum + parseInt(row["number of staff"]?.props?.children || 0), 0
  );
  const totalExpectedPaye = filteredRows.reduce((sum, row) => 
    sum + parseInt(row["expected income from paye"]?.props?.children?.replace(/,/g, '') || 0), 0
  );
  const paidCount = filteredRows.filter(row => 
    row["paye tax status"]?.props?.badgeContent === "paid"
  ).length;

  return (
    <DashboardLayout>
      <DashboardNavbar />
      <ArgonBox py={3}>
        <Card>
          <ArgonBox p={3}>
            <Grid container spacing={3} mb={3}>
              <Grid item xs={12} md={6}>
                <ArgonTypography variant="h6">Hospitals</ArgonTypography>
                <ArgonTypography variant="body2" color="text">
                  Showing {filteredRows.length} hospitals | {totalStaff.toLocaleString()} total staff | 
                  â‚¦{totalExpectedPaye.toLocaleString()} expected PAYE | {paidCount} paid
                </ArgonTypography>
              </Grid>
              <Grid item xs={12} md={6} display="flex" justifyContent="flex-end">
                <TextField
                  placeholder="Search hospitals..."
                  variant="outlined"
                  size="small"
                  value={searchQuery}
                  onChange={handleSearchChange}
                  InputProps={{
                    startAdornment: (
                      <InputAdornment position="start">
                        <SearchIcon />
                      </InputAdornment>
                    ),
                  }}
                />
              </Grid>
            </Grid>

            <Tabs 
              value={tabValue} 
              onChange={handleTabChange} 
              aria-label="hospital type tabs"
              sx={{ borderBottom: 1, borderColor: 'divider', mb: 3 }}
            >
              <Tab label="Public Hospitals" />
              <Tab label="Private Hospitals" />
            </Tabs>

            <ArgonBox display="flex" gap={2} mb={3} flexWrap="wrap">
              <FormControl size="small" sx={{ minWidth: 120 }}>
                <InputLabel>License Status</InputLabel>
                <Select
                  value={licenseFilter}
                  onChange={handleLicenseFilterChange}
                  label="License Status"
                >
                  <MenuItem value="all">All Licenses</MenuItem>
                  <MenuItem value="renewed">Renewed</MenuItem>
                  <MenuItem value="expired">Expired</MenuItem>
                </Select>
              </FormControl>

              <FormControl size="small" sx={{ minWidth: 120 }}>
                <InputLabel>LGA</InputLabel>
                <Select
                  value={lgaFilter}
                  onChange={handleLgaFilterChange}
                  label="LGA"
                >
                  <MenuItem value="all">All LGAs</MenuItem>
                  {uniqueLgas.map(lga => (
                    <MenuItem key={lga} value={lga}>{lga}</MenuItem>
                  ))}
                </Select>
              </FormControl>

              <FormControl size="small" sx={{ minWidth: 120 }}>
                <InputLabel>Staff Size</InputLabel>
                <Select
                  value={staffFilter}
                  onChange={handleStaffFilterChange}
                  label="Staff Size"
                >
                  <MenuItem value="all">All Sizes</MenuItem>
                  <MenuItem value="small">&lt; 100 staff</MenuItem>
                  <MenuItem value="medium">100-200 staff</MenuItem>
                  <MenuItem value="large">&gt; 200 staff</MenuItem>
                </Select>
              </FormControl>

              <FormControl size="small" sx={{ minWidth: 120 }}>
                <InputLabel>PAYE Status</InputLabel>
                <Select
                  value={payeFilter}
                  onChange={handlePayeFilterChange}
                  label="PAYE Status"
                >
                  <MenuItem value="all">All Status</MenuItem>
                  <MenuItem value="paid">Paid</MenuItem>
                  <MenuItem value="unpaid">Unpaid</MenuItem>
                </Select>
              </FormControl>
            </ArgonBox>

            <ArgonBox>
              {columns.map(column => (
                <Tooltip key={column.name} title={`Sort by ${column.name}`}>
                  <IconButton
                    size="small"
                    onClick={() => handleSort(column.name)}
                    sx={{ 
                      opacity: sortConfig.key === column.name ? 1 : 0.5,
                      transform: sortConfig.key === column.name && sortConfig.direction === 'desc' ? 'rotate(180deg)' : 'none'
                    }}
                  >
                    <SortIcon />
                  </IconButton>
                </Tooltip>
              ))}
            </ArgonBox>

            <Table 
              columns={columns} 
              rows={filteredRows}
              noDataText="No hospitals match the current filters"
            />
          </ArgonBox>
        </Card>
      </ArgonBox>
      <Footer />
    </DashboardLayout>
  );
}

export default Hospitals;
