/**
=========================================================
* Argon Dashboard 2 MUI - v3.0.1
=========================================================

* Product Page: https://www.creative-tim.com/product/argon-dashboard-material-ui
* Copyright 2023 Creative Tim (https://www.creative-tim.com)

Coded by www.creative-tim.com

 =========================================================

* The above copyright notice and this permission notice shall be included in all copies or substantial portions of the Software.
*/

import { useState, useMemo } from "react";

// @mui material components
import Grid from "@mui/material/Grid";
import Card from "@mui/material/Card";
import Icon from "@mui/material/Icon";
import Menu from "@mui/material/Menu";
import MenuItem from "@mui/material/MenuItem";
import TextField from "@mui/material/TextField";
import InputAdornment from "@mui/material/InputAdornment";
import SearchIcon from "@mui/icons-material/Search";
import Button from "@mui/material/Button";
import Tabs from "@mui/material/Tabs";
import Tab from "@mui/material/Tab";

// Argon Dashboard 2 MUI components
import ArgonBox from "components/ArgonBox";
import ArgonTypography from "components/ArgonTypography";

// Argon Dashboard 2 MUI examples
import DashboardLayout from "examples/LayoutContainers/DashboardLayout";
import DashboardNavbar from "examples/Navbars/DashboardNavbar";
import Footer from "examples/Footer";
import Table from "examples/Tables/Table";

// Data
import hotelsTableData from "layouts/tables/data/hotelsTableData";

function Hotels() {
  const { columns, rows } = hotelsTableData;
  const [tabValue, setTabValue] = useState(0);
  const [searchQuery, setSearchQuery] = useState("");

  // Filter states and anchors
  const [licenseAnchor, setLicenseAnchor] = useState(null);
  const [locationAnchor, setLocationAnchor] = useState(null);
  const [roomsAnchor, setRoomsAnchor] = useState(null);
  const [payeAnchor, setPayeAnchor] = useState(null);

  const [licenseFilter, setLicenseFilter] = useState("all");
  const [locationFilter, setLocationFilter] = useState("all");
  const [roomsFilter, setRoomsFilter] = useState("all");
  const [payeFilter, setPayeFilter] = useState("all");

  // Stats
  const totalHotels = rows.length;
  const totalStaff = rows.reduce((sum, row) => {
    const staffText = row.staff.props.children;
    return sum + parseInt(staffText);
  }, 0);
  const totalPaye = rows.reduce((sum, row) => {
    const incomeText = row.expected_income.props.children;
    return sum + parseInt(incomeText.replace(/,/g, ''));
  }, 0);
  const paidCount = rows.filter(row => 
    row.paye_status.props.badgeContent === "PAID"
  ).length;

  const standardHotels = rows.filter(row => row.type.props.children === "STANDARD");
  const luxuryHotels = rows.filter(row => row.type.props.children === "LUXURY");
  const budgetHotels = rows.filter(row => row.type.props.children === "BUDGET");

  const handleTabChange = (event, newValue) => {
    setTabValue(newValue);
  };

  // Filter handlers
  const handleFilterClick = (event, setAnchor) => {
    setAnchor(event.currentTarget);
  };

  const handleFilterClose = (setAnchor) => {
    setAnchor(null);
  };

  const handleFilterSelect = (filter, value, setFilter, setAnchor) => {
    setFilter(value);
    setAnchor(null);
  };

  // Get unique values for filters
  const uniqueLocations = [...new Set(rows.map(row => row.location.props.children))];
  const roomsRanges = ["0-50", "51-100", "101-200", "200+"];

  const filteredRows = useMemo(() => {
    let filtered = rows;

    // Filter by search query
    if (searchQuery) {
      filtered = filtered.filter(row => 
        row.hotel.props.children.props.children.toLowerCase().includes(searchQuery.toLowerCase())
      );
    }

    // Filter by tab
    if (tabValue === 0) {
      filtered = filtered.filter(row => row.type.props.children === "STANDARD");
    } else if (tabValue === 1) {
      filtered = filtered.filter(row => row.type.props.children === "LUXURY");
    } else if (tabValue === 2) {
      filtered = filtered.filter(row => row.type.props.children === "BUDGET");
    }

    // Apply license filter
    if (licenseFilter !== "all") {
      filtered = filtered.filter(row => row.license_status.props.badgeContent === licenseFilter);
    }

    // Apply location filter
    if (locationFilter !== "all") {
      filtered = filtered.filter(row => row.location.props.children === locationFilter);
    }

    // Apply rooms filter
    if (roomsFilter !== "all") {
      filtered = filtered.filter(row => {
        const rooms = parseInt(row.number_of_rooms.props.children);
        switch (roomsFilter) {
          case "0-50": return rooms <= 50;
          case "51-100": return rooms > 50 && rooms <= 100;
          case "101-200": return rooms > 100 && rooms <= 200;
          case "200+": return rooms > 200;
          default: return true;
        }
      });
    }

    // Apply PAYE filter
    if (payeFilter !== "all") {
      filtered = filtered.filter(row => row.paye_status.props.badgeContent === payeFilter);
    }

    return filtered;
  }, [rows, searchQuery, tabValue, licenseFilter, locationFilter, roomsFilter, payeFilter]);

  return (
    <DashboardLayout>
      <DashboardNavbar />
      <ArgonBox py={3}>
        <Grid container spacing={3} mb={3}>
          <Grid item xs={12}>
            <Card>
              <ArgonBox p={3}>
                <Grid container spacing={3}>
                  <Grid item xs={12} md={6}>
                    <ArgonTypography variant="h4" fontWeight="medium">
                      Hotels Dashboard
                    </ArgonTypography>
                    <ArgonBox display="flex" gap={2} mt={1}>
                      <ArgonTypography variant="button" fontWeight="regular" color="text">
                        {totalHotels} hotels
                      </ArgonTypography>
                      <ArgonTypography variant="button" fontWeight="regular" color="text">
                        •
                      </ArgonTypography>
                      <ArgonTypography variant="button" fontWeight="regular" color="text">
                        {totalStaff} staff
                      </ArgonTypography>
                      <ArgonTypography variant="button" fontWeight="regular" color="text">
                        •
                      </ArgonTypography>
                      <ArgonTypography variant="button" fontWeight="regular" color="text">
                        ₦{totalPaye.toLocaleString()} PAYE
                      </ArgonTypography>
                      <ArgonTypography variant="button" fontWeight="regular" color="text">
                        •
                      </ArgonTypography>
                      <ArgonTypography variant="button" fontWeight="regular" color="text">
                        {paidCount} paid
                      </ArgonTypography>
                    </ArgonBox>
                  </Grid>
                  <Grid item xs={12} md={6} display="flex" justifyContent="flex-end">
                    <TextField
                      placeholder="Search hotels..."
                      variant="outlined"
                      size="small"
                      value={searchQuery}
                      onChange={(e) => setSearchQuery(e.target.value)}
                      InputProps={{
                        startAdornment: (
                          <InputAdornment position="start">
                            <SearchIcon />
                          </InputAdornment>
                        ),
                      }}
                      sx={{ width: 300 }}
                    />
                  </Grid>
                </Grid>
              </ArgonBox>
            </Card>
          </Grid>
        </Grid>

        <Card>
          <ArgonBox p={3}>
            <Tabs
              value={tabValue}
              onChange={handleTabChange}
              textColor="primary"
              indicatorColor="primary"
            >
              <Tab label={`Standard Hotels (${standardHotels.length})`} />
              <Tab label={`Luxury Hotels (${luxuryHotels.length})`} />
              <Tab label={`Budget Hotels (${budgetHotels.length})`} />
            </Tabs>

            <ArgonBox display="flex" gap={2} mt={3}>
              {/* License Status Filter */}
              <Button
                variant="outlined"
                startIcon={<Icon>settings</Icon>}
                onClick={(e) => handleFilterClick(e, setLicenseAnchor)}
                size="small"
                sx={{
                  color: 'rgb(103, 116, 142)',
                  borderColor: 'rgb(226, 232, 240)',
                  textTransform: 'none',
                  '&:hover': {
                    borderColor: 'rgb(226, 232, 240)',
                    backgroundColor: 'rgba(226, 232, 240, 0.1)',
                  },
                }}
              >
                License Status
              </Button>
              <Menu
                anchorEl={licenseAnchor}
                open={Boolean(licenseAnchor)}
                onClose={() => handleFilterClose(setLicenseAnchor)}
                PaperProps={{
                  sx: {
                    mt: 1,
                    boxShadow: '0px 2px 4px rgba(0,0,0,0.1)',
                  },
                }}
              >
                <MenuItem onClick={() => handleFilterSelect("licenseStatus", "all", setLicenseFilter, setLicenseAnchor)}>
                  All
                </MenuItem>
                <MenuItem onClick={() => handleFilterSelect("licenseStatus", "RENEWED", setLicenseFilter, setLicenseAnchor)}>
                  Renewed
                </MenuItem>
                <MenuItem onClick={() => handleFilterSelect("licenseStatus", "EXPIRED", setLicenseFilter, setLicenseAnchor)}>
                  Expired
                </MenuItem>
              </Menu>

              {/* Location Filter */}
              <Button
                variant="outlined"
                startIcon={<Icon>location_on</Icon>}
                onClick={(e) => handleFilterClick(e, setLocationAnchor)}
                size="small"
                sx={{
                  color: 'rgb(103, 116, 142)',
                  borderColor: 'rgb(226, 232, 240)',
                  textTransform: 'none',
                  '&:hover': {
                    borderColor: 'rgb(226, 232, 240)',
                    backgroundColor: 'rgba(226, 232, 240, 0.1)',
                  },
                }}
              >
                Location
              </Button>
              <Menu
                anchorEl={locationAnchor}
                open={Boolean(locationAnchor)}
                onClose={() => handleFilterClose(setLocationAnchor)}
                PaperProps={{
                  sx: {
                    mt: 1,
                    boxShadow: '0px 2px 4px rgba(0,0,0,0.1)',
                  },
                }}
              >
                <MenuItem onClick={() => handleFilterSelect("location", "all", setLocationFilter, setLocationAnchor)}>
                  All
                </MenuItem>
                {uniqueLocations.map((location) => (
                  <MenuItem key={location} onClick={() => handleFilterSelect("location", location, setLocationFilter, setLocationAnchor)}>
                    {location}
                  </MenuItem>
                ))}
              </Menu>

              {/* Rooms Filter */}
              <Button
                variant="outlined"
                startIcon={<Icon>hotel</Icon>}
                onClick={(e) => handleFilterClick(e, setRoomsAnchor)}
                size="small"
                sx={{
                  color: 'rgb(103, 116, 142)',
                  borderColor: 'rgb(226, 232, 240)',
                  textTransform: 'none',
                  '&:hover': {
                    borderColor: 'rgb(226, 232, 240)',
                    backgroundColor: 'rgba(226, 232, 240, 0.1)',
                  },
                }}
              >
                Number of Rooms
              </Button>
              <Menu
                anchorEl={roomsAnchor}
                open={Boolean(roomsAnchor)}
                onClose={() => handleFilterClose(setRoomsAnchor)}
                PaperProps={{
                  sx: {
                    mt: 1,
                    boxShadow: '0px 2px 4px rgba(0,0,0,0.1)',
                  },
                }}
              >
                <MenuItem onClick={() => handleFilterSelect("rooms", "all", setRoomsFilter, setRoomsAnchor)}>
                  All
                </MenuItem>
                {roomsRanges.map((range) => (
                  <MenuItem key={range} onClick={() => handleFilterSelect("rooms", range, setRoomsFilter, setRoomsAnchor)}>
                    {range}
                  </MenuItem>
                ))}
              </Menu>

              {/* PAYE Status Filter */}
              <Button
                variant="outlined"
                startIcon={<Icon>payment</Icon>}
                onClick={(e) => handleFilterClick(e, setPayeAnchor)}
                size="small"
                sx={{
                  color: 'rgb(103, 116, 142)',
                  borderColor: 'rgb(226, 232, 240)',
                  textTransform: 'none',
                  '&:hover': {
                    borderColor: 'rgb(226, 232, 240)',
                    backgroundColor: 'rgba(226, 232, 240, 0.1)',
                  },
                }}
              >
                PAYE Status
              </Button>
              <Menu
                anchorEl={payeAnchor}
                open={Boolean(payeAnchor)}
                onClose={() => handleFilterClose(setPayeAnchor)}
                PaperProps={{
                  sx: {
                    mt: 1,
                    boxShadow: '0px 2px 4px rgba(0,0,0,0.1)',
                  },
                }}
              >
                <MenuItem onClick={() => handleFilterSelect("payeStatus", "all", setPayeFilter, setPayeAnchor)}>
                  All
                </MenuItem>
                <MenuItem onClick={() => handleFilterSelect("payeStatus", "PAID", setPayeFilter, setPayeAnchor)}>
                  Paid
                </MenuItem>
                <MenuItem onClick={() => handleFilterSelect("payeStatus", "UNPAID", setPayeFilter, setPayeAnchor)}>
                  Unpaid
                </MenuItem>
              </Menu>
            </ArgonBox>
          </ArgonBox>

          <ArgonBox px={3} pb={3}>
            <Table columns={columns} rows={filteredRows} />
          </ArgonBox>
        </Card>
      </ArgonBox>
      <Footer />
    </DashboardLayout>
  );
}

export default Hotels;
