/**
=========================================================
* Argon Dashboard 2 MUI - v3.0.1
=========================================================
*/

import React, { useState, useMemo } from "react";
// @mui material components
import Grid from "@mui/material/Grid";
import Card from "@mui/material/Card";
import Icon from "@mui/material/Icon";
import TextField from "@mui/material/TextField";
import InputAdornment from "@mui/material/InputAdornment";
import SearchIcon from "@mui/icons-material/Search";
import Tabs from "@mui/material/Tabs";
import Tab from "@mui/material/Tab";
import Select from "@mui/material/Select";
import MenuItem from "@mui/material/MenuItem";
import FormControl from "@mui/material/FormControl";
import InputLabel from "@mui/material/InputLabel";
import IconButton from "@mui/material/IconButton";
import SortIcon from "@mui/icons-material/Sort";
import Tooltip from "@mui/material/Tooltip";
import Button from "@mui/material/Button";
import Menu from "@mui/material/Menu";

// Argon Dashboard 2 MUI components
import ArgonBox from "components/ArgonBox";
import ArgonTypography from "components/ArgonTypography";

// Argon Dashboard 2 MUI examples
import DashboardLayout from "examples/LayoutContainers/DashboardLayout";
import DashboardNavbar from "examples/Navbars/DashboardNavbar";
import Footer from "examples/Footer";
import Table from "examples/Tables/Table";

// Data
import hospitalsTableData from "./data/projectsTableData";

function Hospitals() {
  const { columns, rows } = hospitalsTableData;
  const [searchQuery, setSearchQuery] = useState("");
  const [tabValue, setTabValue] = useState(0);

  // Filter states
  const [licenseFilter, setLicenseFilter] = useState("all");
  const [lgaFilter, setLgaFilter] = useState("all");
  const [staffFilter, setStaffFilter] = useState("all");
  const [payeFilter, setPayeFilter] = useState("all");

  // Menu anchors
  const [licenseMenuAnchor, setLicenseMenuAnchor] = useState(null);
  const [lgaMenuAnchor, setLgaMenuAnchor] = useState(null);
  const [staffMenuAnchor, setStaffMenuAnchor] = useState(null);
  const [payeMenuAnchor, setPayeMenuAnchor] = useState(null);

  // Sorting
  const [sortConfig, setSortConfig] = useState({ key: null, direction: 'asc' });

  // Stats
  const totalHospitals = rows.length;
  const totalStaff = rows.reduce((sum, row) => {
    try {
      return sum + parseInt(row["number of staff"].props.children);
    } catch (error) {
      console.error('Error calculating staff:', error);
      return sum;
    }
  }, 0);
  const totalPaye = rows.reduce((sum, row) => {
    try {
      const incomeText = row["expected income from paye"].props.children;
      return sum + parseInt(incomeText.replace(/,/g, ''));
    } catch (error) {
      console.error('Error calculating PAYE:', error);
      return sum;
    }
  }, 0);
  const paidCount = rows.filter(row => {
    try {
      return row["paye tax status"].props.badgeContent === "paid";
    } catch (error) {
      console.error('Error counting paid hospitals:', error);
      return false;
    }
  }).length;

  const publicHospitals = rows.filter(row => {
    try {
      return row.type.props.children === "Public";
    } catch (error) {
      console.error('Error filtering public hospitals:', error);
      return false;
    }
  });
  const privateHospitals = rows.filter(row => {
    try {
      return row.type.props.children === "Private";
    } catch (error) {
      console.error('Error filtering private hospitals:', error);
      return false;
    }
  });

  const handleTabChange = (event, newValue) => {
    setTabValue(newValue);
    // Reset filters when switching tabs
    setLicenseFilter("all");
    setLgaFilter("all");
    setStaffFilter("all");
    setPayeFilter("all");
  };

  // Get unique LGAs for the filter
  const uniqueLgas = useMemo(() => [...new Set(rows.map(row => 
    row.lga?.props?.children?.toString() || ""
  ))].filter(Boolean).sort(), [rows]);

  const filteredRows = useMemo(() => {
    let filtered = rows;

    // Filter by search query
    if (searchQuery) {
      filtered = filtered.filter(row => {
        try {
          const hospitalName = row.hospital[1].toLowerCase();
          return hospitalName.includes(searchQuery.toLowerCase());
        } catch (error) {
          console.error('Error filtering by search query:', error);
          return true;
        }
      });
    }

    // Filter by tab
    if (tabValue === 0) {
      filtered = filtered.filter(row => {
        try {
          return row.type.props.children === "Public";
        } catch (error) {
          console.error('Error filtering public hospitals:', error);
          return false;
        }
      });
    } else if (tabValue === 1) {
      filtered = filtered.filter(row => {
        try {
          return row.type.props.children === "Private";
        } catch (error) {
          console.error('Error filtering private hospitals:', error);
          return false;
        }
      });
    }

    // Apply license filter
    if (licenseFilter !== "all") {
      filtered = filtered.filter(row => {
        try {
          return row["license status"].props.badgeContent === licenseFilter;
        } catch (error) {
          console.error('Error filtering by license status:', error);
          return false;
        }
      });
    }

    // Apply LGA filter
    if (lgaFilter !== "all") {
      filtered = filtered.filter(row => {
        try {
          return row.lga.props.children === lgaFilter;
        } catch (error) {
          console.error('Error filtering by LGA:', error);
          return false;
        }
      });
    }

    // Apply staff size filter
    if (staffFilter !== "all") {
      filtered = filtered.filter(row => {
        try {
          const staffSize = parseInt(row["number of staff"].props.children);
          switch (staffFilter) {
            case "0-50": return staffSize <= 50;
            case "51-100": return staffSize > 50 && staffSize <= 100;
            case "101-200": return staffSize > 100 && staffSize <= 200;
            case "200+": return staffSize > 200;
            default: return true;
          }
        } catch (error) {
          console.error('Error filtering by staff size:', error);
          return false;
        }
      });
    }

    // Apply PAYE filter
    if (payeFilter !== "all") {
      filtered = filtered.filter(row => {
        try {
          return row["paye tax status"].props.badgeContent === payeFilter;
        } catch (error) {
          console.error('Error filtering by PAYE status:', error);
          return false;
        }
      });
    }

    return filtered;
  }, [rows, searchQuery, tabValue, licenseFilter, lgaFilter, staffFilter, payeFilter]);

  const handleLicenseFilterChange = (event) => {
    setLicenseFilter(event.target.value);
  };

  const handleLgaFilterChange = (event) => {
    setLgaFilter(event.target.value);
  };

  const handleStaffFilterChange = (event) => {
    setStaffFilter(event.target.value);
  };

  const handlePayeFilterChange = (event) => {
    setPayeFilter(event.target.value);
  };

  const handleSort = (key) => {
    setSortConfig(prevConfig => ({
      key,
      direction: prevConfig.key === key && prevConfig.direction === 'asc' ? 'desc' : 'asc'
    }));
  };

  const sortedRows = useMemo(() => {
    let sortableRows = [...filteredRows];
    if (sortConfig.key) {
      sortableRows.sort((a, b) => {
        let aValue = a[sortConfig.key]?.props?.children;
        let bValue = b[sortConfig.key]?.props?.children;

        // Handle array case (e.g., hospital name)
        if (Array.isArray(a[sortConfig.key])) {
          aValue = a[sortConfig.key][1];
          bValue = b[sortConfig.key][1];
        }

        // Handle badge content case
        if (a[sortConfig.key]?.props?.badgeContent) {
          aValue = a[sortConfig.key].props.badgeContent;
          bValue = b[sortConfig.key].props.badgeContent;
        }

        // Numeric comparison
        if (!isNaN(aValue) && !isNaN(bValue)) {
          aValue = Number(aValue);
          bValue = Number(bValue);
        }

        return sortConfig.direction === 'asc' 
          ? (aValue > bValue ? 1 : -1) 
          : (aValue < bValue ? 1 : -1);
      });
    }
    return sortableRows;
  }, [filteredRows, sortConfig]);

  return (
    <DashboardLayout>
      <DashboardNavbar />
      <ArgonBox py={3}>
        <Grid container spacing={3} mb={3}>
          <Grid item xs={12}>
            <Card>
              <ArgonBox p={3}>
                <Grid container spacing={3}>
                  <Grid item xs={12} md={6}>
                    <ArgonTypography variant="h4" fontWeight="medium">
                      Hospitals Dashboard
                    </ArgonTypography>
                    <ArgonBox display="flex" gap={2} mt={1}>
                      <ArgonTypography variant="button" fontWeight="regular" color="text">
                        {totalHospitals} hospitals
                      </ArgonTypography>
                      <ArgonTypography variant="button" fontWeight="regular" color="text">
                        •
                      </ArgonTypography>
                      <ArgonTypography variant="button" fontWeight="regular" color="text">
                        {totalStaff} staff
                      </ArgonTypography>
                      <ArgonTypography variant="button" fontWeight="regular" color="text">
                        •
                      </ArgonTypography>
                      <ArgonTypography variant="button" fontWeight="regular" color="text">
                        ₦{totalPaye.toLocaleString()} PAYE
                      </ArgonTypography>
                      <ArgonTypography variant="button" fontWeight="regular" color="text">
                        •
                      </ArgonTypography>
                      <ArgonTypography variant="button" fontWeight="regular" color="text">
                        {paidCount} paid
                      </ArgonTypography>
                    </ArgonBox>
                  </Grid>
                  <Grid item xs={12} md={6} display="flex" justifyContent="flex-end">
                    <TextField
                      placeholder="Search hospitals..."
                      variant="outlined"
                      size="small"
                      value={searchQuery}
                      onChange={(e) => setSearchQuery(e.target.value)}
                      InputProps={{
                        startAdornment: (
                          <InputAdornment position="start">
                            <SearchIcon />
                          </InputAdornment>
                        ),
                      }}
                      sx={{ width: 300 }}
                    />
                  </Grid>
                </Grid>
              </ArgonBox>
            </Card>
          </Grid>
        </Grid>

        <Card>
          <ArgonBox>
            <Tabs
              value={tabValue}
              onChange={handleTabChange}
              aria-label="hospital type tabs"
              sx={{ 
                borderBottom: '1px solid', 
                borderColor: 'divider',
                px: 3,
                pt: 2 
              }}
            >
              <Tab label="Public Hospitals" />
              <Tab label="Private Hospitals" />
            </Tabs>
          </ArgonBox>

          <ArgonBox p={3}>
            <ArgonBox 
              display="flex" 
              gap={2} 
              mb={3} 
              flexWrap="wrap"
            >
              {/* License Status Filter */}
              <Button
                variant="outlined"
                startIcon={<Icon>verified</Icon>}
                onClick={(event) => setLicenseMenuAnchor(event.currentTarget)}
              >
                {licenseFilter === 'all' ? 'License Status' : 
                 licenseFilter === 'renewed' ? 'Renewed' : 'Expired'}
              </Button>
              <Menu
                anchorEl={licenseMenuAnchor}
                open={Boolean(licenseMenuAnchor)}
                onClose={() => setLicenseMenuAnchor(null)}
              >
                <MenuItem 
                  onClick={() => {
                    setLicenseFilter('all');
                    setLicenseMenuAnchor(null);
                  }}
                  selected={licenseFilter === 'all'}
                >
                  All Licenses
                </MenuItem>
                <MenuItem 
                  onClick={() => {
                    setLicenseFilter('renewed');
                    setLicenseMenuAnchor(null);
                  }}
                  selected={licenseFilter === 'renewed'}
                >
                  Renewed
                </MenuItem>
                <MenuItem 
                  onClick={() => {
                    setLicenseFilter('expired');
                    setLicenseMenuAnchor(null);
                  }}
                  selected={licenseFilter === 'expired'}
                >
                  Expired
                </MenuItem>
              </Menu>

              {/* LGA Filter */}
              <Button
                variant="outlined"
                startIcon={<Icon>location_on</Icon>}
                onClick={(event) => setLgaMenuAnchor(event.currentTarget)}
              >
                {lgaFilter === 'all' ? 'LGA' : lgaFilter}
              </Button>
              <Menu
                anchorEl={lgaMenuAnchor}
                open={Boolean(lgaMenuAnchor)}
                onClose={() => setLgaMenuAnchor(null)}
              >
                <MenuItem 
                  onClick={() => {
                    setLgaFilter('all');
                    setLgaMenuAnchor(null);
                  }}
                  selected={lgaFilter === 'all'}
                >
                  All LGAs
                </MenuItem>
                {uniqueLgas.map(lga => (
                  <MenuItem 
                    key={lga} 
                    onClick={() => {
                      setLgaFilter(lga);
                      setLgaMenuAnchor(null);
                    }}
                    selected={lgaFilter === lga}
                  >
                    {lga}
                  </MenuItem>
                ))}
              </Menu>

              {/* Staff Size Filter */}
              <Button
                variant="outlined"
                startIcon={<Icon>people</Icon>}
                onClick={(event) => setStaffMenuAnchor(event.currentTarget)}
              >
                {staffFilter === 'all' ? 'Staff Size' : 
                 staffFilter === '0-50' ? '0-50 staff' :
                 staffFilter === '51-100' ? '51-100 staff' :
                 staffFilter === '101-200' ? '101-200 staff' : '200+ staff'}
              </Button>
              <Menu
                anchorEl={staffMenuAnchor}
                open={Boolean(staffMenuAnchor)}
                onClose={() => setStaffMenuAnchor(null)}
              >
                <MenuItem 
                  onClick={() => {
                    setStaffFilter('all');
                    setStaffMenuAnchor(null);
                  }}
                  selected={staffFilter === 'all'}
                >
                  All Sizes
                </MenuItem>
                <MenuItem 
                  onClick={() => {
                    setStaffFilter('0-50');
                    setStaffMenuAnchor(null);
                  }}
                  selected={staffFilter === '0-50'}
                >
                  0-50 staff
                </MenuItem>
                <MenuItem 
                  onClick={() => {
                    setStaffFilter('51-100');
                    setStaffMenuAnchor(null);
                  }}
                  selected={staffFilter === '51-100'}
                >
                  51-100 staff
                </MenuItem>
                <MenuItem 
                  onClick={() => {
                    setStaffFilter('101-200');
                    setStaffMenuAnchor(null);
                  }}
                  selected={staffFilter === '101-200'}
                >
                  101-200 staff
                </MenuItem>
                <MenuItem 
                  onClick={() => {
                    setStaffFilter('200+');
                    setStaffMenuAnchor(null);
                  }}
                  selected={staffFilter === '200+'}
                >
                  200+ staff
                </MenuItem>
              </Menu>

              {/* PAYE Status Filter */}
              <Button
                variant="outlined"
                startIcon={<Icon>payment</Icon>}
                onClick={(event) => setPayeMenuAnchor(event.currentTarget)}
              >
                {payeFilter === 'all' ? 'PAYE Status' : 
                 payeFilter === 'paid' ? 'Paid' : 'Unpaid'}
              </Button>
              <Menu
                anchorEl={payeMenuAnchor}
                open={Boolean(payeMenuAnchor)}
                onClose={() => setPayeMenuAnchor(null)}
              >
                <MenuItem 
                  onClick={() => {
                    setPayeFilter('all');
                    setPayeMenuAnchor(null);
                  }}
                  selected={payeFilter === 'all'}
                >
                  All Status
                </MenuItem>
                <MenuItem 
                  onClick={() => {
                    setPayeFilter('paid');
                    setPayeMenuAnchor(null);
                  }}
                  selected={payeFilter === 'paid'}
                >
                  Paid
                </MenuItem>
                <MenuItem 
                  onClick={() => {
                    setPayeFilter('unpaid');
                    setPayeMenuAnchor(null);
                  }}
                  selected={payeFilter === 'unpaid'}
                >
                  Unpaid
                </MenuItem>
              </Menu>

              {/* Sorting */}
              <ArgonBox 
                display="flex" 
                gap={1} 
                mb={2}
                sx={{
                  borderBottom: '1px solid',
                  borderColor: 'grey.300',
                  pb: 1
                }}
              >
                {columns.map(column => (
                  <Tooltip 
                    key={column.name} 
                    title={`Sort by ${column.name}${sortConfig.key === column.name ? 
                      ` (${sortConfig.direction === 'asc' ? 'ascending' : 'descending'})` : ''}`}
                  >
                    <IconButton
                      size="small"
                      onClick={() => handleSort(column.name)}
                      sx={{ 
                        opacity: sortConfig.key === column.name ? 1 : 0.5,
                        transform: sortConfig.key === column.name && sortConfig.direction === 'desc' ? 'rotate(180deg)' : 'none',
                        transition: 'all 0.2s ease-in-out',
                      }}
                    >
                      <SortIcon fontSize="small" />
                    </IconButton>
                  </Tooltip>
                ))}
              </ArgonBox>
            </ArgonBox>

            <Table 
              columns={columns} 
              rows={sortedRows} 
            />
          </ArgonBox>
        </Card>
      </ArgonBox>
      <Footer />
    </DashboardLayout>
  );
}

export default Hospitals;
