/**
=========================================================
* Argon Dashboard 2 MUI - v3.0.1
=========================================================

* Product Page: https://www.creative-tim.com/product/argon-dashboard-material-ui
* Copyright 2023 Creative Tim (https://www.creative-tim.com)

Coded by www.creative-tim.com

 =========================================================

* The above copyright notice and this permission notice shall be included in all copies or substantial portions of the Software.
*/

import { useState, useMemo } from "react";

// @mui material components
import Grid from "@mui/material/Grid";
import Card from "@mui/material/Card";
import Icon from "@mui/material/Icon";
import Menu from "@mui/material/Menu";
import MenuItem from "@mui/material/MenuItem";
import TextField from "@mui/material/TextField";
import InputAdornment from "@mui/material/InputAdornment";
import SearchIcon from "@mui/icons-material/Search";
import Button from "@mui/material/Button";
import FilterListIcon from "@mui/icons-material/FilterList";
import LocationOnIcon from "@mui/icons-material/LocationOn";
import SchoolIcon from "@mui/icons-material/School";
import WorkIcon from "@mui/icons-material/Work";
import PaymentIcon from "@mui/icons-material/Payment";

// Argon Dashboard 2 MUI components
import ArgonBox from "components/ArgonBox";
import ArgonTypography from "components/ArgonTypography";
import ArgonBadge from "components/ArgonBadge";

// Argon Dashboard 2 MUI examples
import DashboardLayout from "examples/LayoutContainers/DashboardLayout";
import DashboardNavbar from "examples/Navbars/DashboardNavbar";
import Footer from "examples/Footer";
import Table from "examples/Tables/Table";

// Data
import authorsTableData from "./data/authorsTableData";

function Schools() {
  // Safely destructure the data with default values
  const { columns = [], rows = [] } = authorsTableData || {};

  // Filter states
  const [searchQuery, setSearchQuery] = useState("");
  const [licenseFilter, setLicenseFilter] = useState("all");
  const [typeFilter, setTypeFilter] = useState("all");
  const [lgaFilter, setLgaFilter] = useState("all");
  const [payeFilter, setPayeFilter] = useState("all");
  const [staffSizeFilter, setStaffSizeFilter] = useState("all");

  // Menu anchor states
  const [licenseAnchor, setLicenseAnchor] = useState(null);
  const [typeAnchor, setTypeAnchor] = useState(null);
  const [lgaAnchor, setLgaAnchor] = useState(null);
  const [payeAnchor, setPayeAnchor] = useState(null);
  const [staffSizeAnchor, setStaffSizeAnchor] = useState(null);

  // Get unique LGAs from data
  const uniqueLgas = useMemo(() => {
    const lgas = new Set(rows.map(row => row.lga.props.children));
    return Array.from(lgas);
  }, [rows]);

  const getSchoolName = (schoolElement) => {
    try {
      return schoolElement?.props?.children?.props?.children || "";
    } catch (e) {
      return "";
    }
  };

  // Filter handlers
  const handleSearchChange = (event) => {
    setSearchQuery(event.target.value.toLowerCase());
  };

  const handleLicenseFilter = (value) => {
    setLicenseFilter(value);
    setLicenseAnchor(null);
  };

  const handleTypeFilter = (value) => {
    setTypeFilter(value);
    setTypeAnchor(null);
  };

  const handleLgaFilter = (value) => {
    setLgaFilter(value);
    setLgaAnchor(null);
  };

  const handlePayeFilter = (value) => {
    setPayeFilter(value);
    setPayeAnchor(null);
  };

  const handleStaffSizeFilter = (value) => {
    setStaffSizeFilter(value);
    setStaffSizeAnchor(null);
  };

  // Filter the rows based on all criteria
  const filteredRows = useMemo(() => {
    // Validate rows
    if (!rows) {
      console.log('No rows data');
      return [];
    }

    // Debug rows data
    console.log('Rows data:', rows);

    return (rows || []).filter((row) => {
      // Skip filtering if no search query
      if (!searchQuery) return true;

      try {
        // Debug individual row
        console.log('Processing row:', row);

        // Check if row exists
        if (!row) return false;

        // Extract school name text directly from the row structure
        const schoolText = row.school?.props?.children?.props?.children || '';

        // Debug school text
        console.log('School text:', schoolText);

        // Perform case-insensitive search
        return schoolText.toString().toLowerCase().includes(searchQuery.toLowerCase());
      } catch (err) {
        console.error('Error processing row:', err);
        return false;
      }
    });
  }, [rows, searchQuery]);

  // Statistics
  const stats = useMemo(() => {
    const total = filteredRows.length;
    const publicCount = filteredRows.filter(row => row.type.props.type === "public").length;
    const privateCount = filteredRows.filter(row => row.type.props.type === "private").length;
    const renewedCount = filteredRows.filter(row => row["license status"].props.badgeContent === "renewed").length;
    const expiredCount = filteredRows.filter(row => row["license status"].props.badgeContent === "expired").length;

    return { total, publicCount, privateCount, renewedCount, expiredCount };
  }, [filteredRows]);

  return (
    <DashboardLayout>
      <DashboardNavbar />
      <ArgonBox py={3}>
        <ArgonBox mb={3}>
          <Grid container spacing={3}>
            <Grid item xs={12} md={6} lg={3}>
              <ArgonBox mb={3}>
                <ArgonTypography variant="h6" fontWeight="medium">
                  Total Schools: {stats.total}
                </ArgonTypography>
              </ArgonBox>
            </Grid>
            <Grid item xs={12} md={6} lg={3}>
              <ArgonBox mb={3}>
                <ArgonTypography variant="h6" fontWeight="medium">
                  Public Schools: {stats.publicCount}
                </ArgonTypography>
              </ArgonBox>
            </Grid>
            <Grid item xs={12} md={6} lg={3}>
              <ArgonBox mb={3}>
                <ArgonTypography variant="h6" fontWeight="medium">
                  Private Schools: {stats.privateCount}
                </ArgonTypography>
              </ArgonBox>
            </Grid>
            <Grid item xs={12} md={6} lg={3}>
              <ArgonBox mb={3}>
                <ArgonTypography variant="h6" fontWeight="medium">
                  Expired Licenses: {stats.expiredCount}
                </ArgonTypography>
              </ArgonBox>
            </Grid>
          </Grid>
          <Card>
            <ArgonBox display="flex" justifyContent="space-between" alignItems="center" p={3}>
              <ArgonBox display="flex" gap={2}>
                {/* License Status Filter */}
                <Button
                  variant="outlined"
                  onClick={(e) => setLicenseAnchor(e.currentTarget)}
                  startIcon={<FilterListIcon />}
                  sx={{ borderRadius: "20px" }}
                >
                  License: {licenseFilter}
                </Button>
                <Menu
                  anchorEl={licenseAnchor}
                  open={Boolean(licenseAnchor)}
                  onClose={() => setLicenseAnchor(null)}
                >
                  <MenuItem onClick={() => handleLicenseFilter("all")}>All</MenuItem>
                  <MenuItem onClick={() => handleLicenseFilter("renewed")}>Renewed</MenuItem>
                  <MenuItem onClick={() => handleLicenseFilter("expired")}>Expired</MenuItem>
                  <MenuItem onClick={() => handleLicenseFilter("exempted")}>Exempted</MenuItem>
                </Menu>

                {/* School Type Filter */}
                <Button
                  variant="outlined"
                  onClick={(e) => setTypeAnchor(e.currentTarget)}
                  startIcon={<SchoolIcon />}
                  sx={{ borderRadius: "20px" }}
                >
                  Type: {typeFilter}
                </Button>
                <Menu
                  anchorEl={typeAnchor}
                  open={Boolean(typeAnchor)}
                  onClose={() => setTypeAnchor(null)}
                >
                  <MenuItem onClick={() => handleTypeFilter("all")}>All</MenuItem>
                  <MenuItem onClick={() => handleTypeFilter("public")}>Public</MenuItem>
                  <MenuItem onClick={() => handleTypeFilter("private")}>Private</MenuItem>
                </Menu>

                {/* LGA Filter */}
                <Button
                  variant="outlined"
                  onClick={(e) => setLgaAnchor(e.currentTarget)}
                  startIcon={<LocationOnIcon />}
                  sx={{ borderRadius: "20px" }}
                >
                  LGA: {lgaFilter}
                </Button>
                <Menu
                  anchorEl={lgaAnchor}
                  open={Boolean(lgaAnchor)}
                  onClose={() => setLgaAnchor(null)}
                >
                  <MenuItem onClick={() => handleLgaFilter("all")}>All</MenuItem>
                  {uniqueLgas.map((lga) => (
                    <MenuItem key={lga} onClick={() => handleLgaFilter(lga)}>{lga}</MenuItem>
                  ))}
                </Menu>

                {/* PAYE Status Filter */}
                <Button
                  variant="outlined"
                  onClick={(e) => setPayeAnchor(e.currentTarget)}
                  startIcon={<PaymentIcon />}
                  sx={{ borderRadius: "20px" }}
                >
                  PAYE: {payeFilter}
                </Button>
                <Menu
                  anchorEl={payeAnchor}
                  open={Boolean(payeAnchor)}
                  onClose={() => setPayeAnchor(null)}
                >
                  <MenuItem onClick={() => handlePayeFilter("all")}>All</MenuItem>
                  <MenuItem onClick={() => handlePayeFilter("paid")}>Paid</MenuItem>
                  <MenuItem onClick={() => handlePayeFilter("unpaid")}>Unpaid</MenuItem>
                </Menu>

                {/* Staff Size Filter */}
                <Button
                  variant="outlined"
                  onClick={(e) => setStaffSizeAnchor(e.currentTarget)}
                  startIcon={<WorkIcon />}
                  sx={{ borderRadius: "20px" }}
                >
                  Staff: {staffSizeFilter}
                </Button>
                <Menu
                  anchorEl={staffSizeAnchor}
                  open={Boolean(staffSizeAnchor)}
                  onClose={() => setStaffSizeAnchor(null)}
                >
                  <MenuItem onClick={() => handleStaffSizeFilter("all")}>All</MenuItem>
                  <MenuItem onClick={() => handleStaffSizeFilter("lt100")}>{"< 100"}</MenuItem>
                  <MenuItem onClick={() => handleStaffSizeFilter("100to200")}>100-200</MenuItem>
                  <MenuItem onClick={() => handleStaffSizeFilter("gt200")}>{"> 200"}</MenuItem>
                </Menu>
              </ArgonBox>

              {/* Search Field */}
              <TextField
                placeholder="Search schools..."
                variant="outlined"
                size="small"
                value={searchQuery}
                onChange={handleSearchChange}
                InputProps={{
                  startAdornment: (
                    <InputAdornment position="start">
                      <SearchIcon />
                    </InputAdornment>
                  ),
                }}
                sx={{ minWidth: "200px" }}
              />
            </ArgonBox>

            <ArgonBox
              sx={{
                "& .MuiTableRow-root:not(:last-child)": {
                  "& td": {
                    borderBottom: ({ borders: { borderWidth, borderColor } }) =>
                      `${borderWidth[1]} solid ${borderColor}`,
                  },
                },
              }}
            >
              <Table columns={columns} rows={filteredRows} />
            </ArgonBox>
          </Card>
        </ArgonBox>
      </ArgonBox>
      <Footer />
    </DashboardLayout>
  );
}

export default Schools;
