/**
=========================================================
* Argon Dashboard 2 MUI - v3.0.1
=========================================================
*/

import React, { useState, useMemo } from "react";
// @mui material components
import Card from "@mui/material/Card";
import TextField from "@mui/material/TextField";
import InputAdornment from "@mui/material/InputAdornment";
import SearchIcon from "@mui/icons-material/Search";
import Tabs from "@mui/material/Tabs";
import Tab from "@mui/material/Tab";
import Select from "@mui/material/Select";
import MenuItem from "@mui/material/MenuItem";
import FormControl from "@mui/material/FormControl";
import InputLabel from "@mui/material/InputLabel";
import Grid from "@mui/material/Grid";
import IconButton from "@mui/material/IconButton";
import SortIcon from "@mui/icons-material/Sort";
import Tooltip from "@mui/material/Tooltip";
import Button from "@mui/material/Button";
import Menu from "@mui/material/Menu";
import Icon from "@mui/icons-material/Verified";
import Modal from "@mui/material/Modal";
import Box from "@mui/material/Box";

// Argon Dashboard 2 MUI components
import ArgonBox from "components/ArgonBox";
import ArgonTypography from "components/ArgonTypography";
import ArgonBadge from "components/ArgonBadge";
import ArgonButton from "components/ArgonButton";

// Argon Dashboard 2 MUI examples
import DashboardLayout from "examples/LayoutContainers/DashboardLayout";
import DashboardNavbar from "examples/Navbars/DashboardNavbar";
import Footer from "examples/Footer";
import Table from "examples/Tables/Table";

// Data
import hospitalsTableData from "./data/projectsTableData";

function MapModal({ isOpen, onClose, location, hospitalName }) {
  return (
    <Modal
      open={isOpen}
      onClose={onClose}
      aria-labelledby="map-modal-title"
      aria-describedby="map-modal-description"
    >
      <Box sx={{
        position: 'absolute',
        top: '50%',
        left: '50%',
        transform: 'translate(-50%, -50%)',
        width: '80vw',
        maxWidth: '800px',
        height: '70vh',
        bgcolor: 'background.paper',
        borderRadius: '10px',
        boxShadow: '0 8px 32px 0 rgba(0,0,0,0.1)',
        p: 0,
        overflow: 'hidden'
      }}>
        <ArgonBox 
          p={2} 
          borderBottom="1px solid" 
          borderColor="rgb(226, 232, 240)"
          display="flex"
          justifyContent="space-between"
          alignItems="center"
        >
          <ArgonTypography variant="h6" fontWeight="medium">
            {hospitalName} Location
          </ArgonTypography>
          <ArgonButton 
            variant="text" 
            color="primary"
            onClick={onClose}
            sx={{ minWidth: 'auto', p: 1 }}
          >
            <Icon sx={{ color: 'rgb(103, 116, 142)' }}>close</Icon>
          </ArgonButton>
        </ArgonBox>
        <Box sx={{ width: '100%', height: 'calc(100% - 60px)' }}>
          <iframe
            width="100%"
            height="100%"
            frameBorder="0"
            style={{ border: 0 }}
            src={`https://www.google.com/maps/embed/v1/place?key=YOUR_GOOGLE_MAPS_API_KEY&q=${encodeURIComponent(location + ', Nigeria')}`}
            allowFullScreen
          ></iframe>
        </Box>
      </Box>
    </Modal>
  );
}

function LocationButton({ location, hospitalName }) {
  const [isModalOpen, setIsModalOpen] = useState(false);

  return (
    <>
      <ArgonButton
        variant="text"
        color="primary"
        onClick={() => setIsModalOpen(true)}
        sx={{ minWidth: 'auto' }}
      >
        <Icon sx={{ color: 'rgb(103, 116, 142)' }}>location_on</Icon>
      </ArgonButton>
      <MapModal
        isOpen={isModalOpen}
        onClose={() => setIsModalOpen(false)}
        location={location}
        hospitalName={hospitalName}
      />
    </>
  );
}

function Hospitals() {
  const { columns, rows } = hospitalsTableData;
  const [tabValue, setTabValue] = useState(0);
  const [searchQuery, setSearchQuery] = useState("");
  const [licenseFilter, setLicenseFilter] = useState("all");
  const [lgaFilter, setLgaFilter] = useState("all");
  const [staffFilter, setStaffFilter] = useState("all");
  const [payeFilter, setPayeFilter] = useState("all");
  const [sortConfig, setSortConfig] = useState({ key: null, direction: 'asc' });
  const [licenseMenuAnchor, setLicenseMenuAnchor] = useState(null);
  const [lgaMenuAnchor, setLgaMenuAnchor] = useState(null);
  const [staffMenuAnchor, setStaffMenuAnchor] = useState(null);
  const [payeMenuAnchor, setPayeMenuAnchor] = useState(null);

  // Error boundary state
  const [error, setError] = useState(null);

  const formatCurrency = (amount) => {
    try {
      return new Intl.NumberFormat('en-NG', {
        style: 'currency',
        currency: 'NGN',
        minimumFractionDigits: 0,
        maximumFractionDigits: 0
      }).format(amount);
    } catch (err) {
      console.error('Currency formatting error:', err);
      return `₦${amount.toLocaleString()}`;
    }
  };

  const handleTabChange = (event, newValue) => {
    setTabValue(newValue);
    // Reset filters when switching tabs
    setLicenseFilter("all");
    setLgaFilter("all");
    setStaffFilter("all");
    setPayeFilter("all");
    setSortConfig({ key: null, direction: 'asc' });
  };

  const handleSearchChange = (event) => {
    setSearchQuery(event.target.value.toLowerCase());
  };

  const handleLicenseFilterChange = (event) => {
    setLicenseFilter(event.target.value);
  };

  const handleLgaFilterChange = (event) => {
    setLgaFilter(event.target.value);
  };

  const handleStaffFilterChange = (event) => {
    setStaffFilter(event.target.value);
  };

  const handlePayeFilterChange = (event) => {
    setPayeFilter(event.target.value);
  };

  const handleSort = (key) => {
    setSortConfig(prevConfig => ({
      key,
      direction: prevConfig.key === key && prevConfig.direction === 'asc' ? 'desc' : 'asc'
    }));
  };

  // Get unique LGAs for the filter
  const uniqueLgas = [...new Set(rows.map(row => 
    row.lga?.props?.children?.toString() || ""
  ))].filter(Boolean).sort();

  let filteredRows = rows.filter((row) => {
    try {
      // Hospital type filter (tabs)
      const typeMatch = tabValue === 0 ? 
        row.type?.props?.type === "public" : 
        row.type?.props?.type === "private";

      // Search filter across all fields
      const searchMatch = Object.entries(row).some(([key, value]) => {
        if (Array.isArray(value)) {
          return value[1]?.toString().toLowerCase().includes(searchQuery);
        }
        return value?.props?.children?.toString().toLowerCase().includes(searchQuery) ||
               value?.props?.badgeContent?.toString().toLowerCase().includes(searchQuery);
      });

      // License status filter
      const licenseMatch = licenseFilter === "all" || 
        row["license status"]?.props?.badgeContent === licenseFilter;

      // LGA filter
      const lgaMatch = lgaFilter === "all" || 
        row.lga?.props?.children === lgaFilter;

      // Staff size filter
      let staffMatch = true;
      if (staffFilter !== "all") {
        const staffCount = parseInt(row["number of staff"]?.props?.children);
        switch (staffFilter) {
          case "small":
            staffMatch = staffCount < 100;
            break;
          case "medium":
            staffMatch = staffCount >= 100 && staffCount < 200;
            break;
          case "large":
            staffMatch = staffCount >= 200;
            break;
        }
      }

      // PAYE status filter
      const payeMatch = payeFilter === "all" || 
        row["paye tax status"]?.props?.badgeContent === payeFilter;

      return typeMatch && searchMatch && licenseMatch && lgaMatch && staffMatch && payeMatch;
    } catch (err) {
      console.error('Error filtering row:', err);
      setError('An error occurred while filtering the data. Please try refreshing the page.');
      return false;
    }
  });

  // Apply sorting if configured
  if (sortConfig.key) {
    try {
      filteredRows.sort((a, b) => {
        let aValue = a[sortConfig.key]?.props?.children;
        let bValue = b[sortConfig.key]?.props?.children;

        // Handle array case (e.g., hospital name)
        if (Array.isArray(a[sortConfig.key])) {
          aValue = a[sortConfig.key][1];
          bValue = b[sortConfig.key][1];
        }

        // Handle badge content case
        if (a[sortConfig.key]?.props?.badgeContent) {
          aValue = a[sortConfig.key].props.badgeContent;
          bValue = b[sortConfig.key].props.badgeContent;
        }

        // Handle numeric values (including currency)
        if (typeof aValue === 'string' && aValue.includes('₦')) {
          aValue = Number(aValue.replace(/[₦,]/g, ''));
          bValue = Number(bValue.replace(/[₦,]/g, ''));
        } else if (!isNaN(aValue)) {
          aValue = Number(aValue);
          bValue = Number(bValue);
        }

        if (aValue < bValue) return sortConfig.direction === 'asc' ? -1 : 1;
        if (aValue > bValue) return sortConfig.direction === 'asc' ? 1 : -1;
        return 0;
      });
    } catch (err) {
      console.error('Error sorting data:', err);
      setError('An error occurred while sorting the data. Please try refreshing the page.');
    }
  }

  // Calculate summary statistics
  const totalStaff = filteredRows.reduce((sum, row) => {
    try {
      return sum + parseInt(row["number of staff"]?.props?.children || 0);
    } catch (err) {
      console.error('Error calculating total staff:', err);
      return sum;
    }
  }, 0);

  const totalExpectedPaye = filteredRows.reduce((sum, row) => {
    try {
      return sum + parseInt(row["expected income from paye"]?.props?.children?.replace(/[₦,]/g, '') || 0);
    } catch (err) {
      console.error('Error calculating total PAYE:', err);
      return sum;
    }
  }, 0);

  const paidCount = filteredRows.filter(row => {
    try {
      return row["paye tax status"]?.props?.badgeContent === "paid";
    } catch (err) {
      console.error('Error counting paid hospitals:', err);
      return false;
    }
  }).length;

  if (error) {
    return (
      <DashboardLayout>
        <DashboardNavbar />
        <ArgonBox py={3}>
          <Card>
            <ArgonBox p={3}>
              <ArgonTypography variant="h6" color="error">
                {error}
              </ArgonTypography>
            </ArgonBox>
          </Card>
        </ArgonBox>
        <Footer />
      </DashboardLayout>
    );
  }

  const tableData = useMemo(() => {
    return {
      columns: [
        { name: "hospital", align: "left" },
        { name: "type", align: "left" },
        { name: "location", align: "center" },
        { name: "beds", align: "center" },
        { name: "staff", align: "center" },
        { name: "expected_income", align: "center" },
        { name: "license_status", align: "center" },
        { name: "paye_status", align: "center" },
        { name: "action", align: "center" },
        { name: "map", align: "center" }
      ],
      rows: filteredRows.map(row => ({
        hospital: (
          <ArgonBox display="flex" alignItems="center" px={1} py={0.5}>
            <ArgonBox display="flex" flexDirection="column">
              <ArgonTypography variant="button" fontWeight="medium">
                {row.name}
              </ArgonTypography>
            </ArgonBox>
          </ArgonBox>
        ),
        type: (
          <ArgonTypography variant="caption" color="text" fontWeight="regular">
            {row.type}
          </ArgonTypography>
        ),
        location: (
          <ArgonTypography variant="caption" color="text" fontWeight="regular">
            {row.lga}
          </ArgonTypography>
        ),
        beds: (
          <ArgonTypography variant="caption" color="text" fontWeight="regular">
            {row.beds}
          </ArgonTypography>
        ),
        staff: (
          <ArgonTypography variant="caption" color="text" fontWeight="regular">
            {row.staff}
          </ArgonTypography>
        ),
        expected_income: (
          <ArgonTypography variant="caption" color="text" fontWeight="regular">
            {formatCurrency(row.expectedIncome)}
          </ArgonTypography>
        ),
        license_status: (
          <ArgonBadge
            variant="contained"
            color={row.licenseStatus === "RENEWED" ? "success" : "error"}
            size="xs"
            badgeContent={row.licenseStatus}
            container
          />
        ),
        paye_status: (
          <ArgonBadge
            variant="contained"
            color={row.payeStatus === "PAID" ? "success" : "error"}
            size="xs"
            badgeContent={row.payeStatus}
            container
          />
        ),
        action: (
          <ArgonBox display="flex" alignItems="center" mt={{ xs: 2, sm: 0 }} ml={{ xs: -1.5, sm: 0 }}>
            <ArgonBox mr={1}>
              <ArgonButton variant="text" color="primary">
                <Icon sx={{ color: 'rgb(103, 116, 142)' }}>edit</Icon>
              </ArgonButton>
            </ArgonBox>
            <ArgonButton variant="text" color="error">
              <Icon sx={{ color: 'rgb(103, 116, 142)' }}>delete</Icon>
            </ArgonButton>
          </ArgonBox>
        ),
        map: (
          <LocationButton 
            location={`${row.name}, ${row.lga}`} 
            hospitalName={row.name}
          />
        )
      }))
    };
  }, [filteredRows]);

  return (
    <DashboardLayout>
      <DashboardNavbar />
      <ArgonBox py={3}>
        <Card>
          <ArgonBox p={3}>
            {/* Header Section */}
            <Grid container spacing={3} alignItems="center" mb={3}>
              <Grid item xs={12} md={6}>
                <ArgonTypography variant="h5" fontWeight="bold" mb={1}>
                  Hospitals Dashboard
                </ArgonTypography>
                <ArgonBox display="flex" alignItems="center" gap={2}>
                  <ArgonTypography variant="button" fontWeight="regular" color="text">
                    {filteredRows.length} hospitals
                  </ArgonTypography>
                  <ArgonBox sx={{ width: '4px', height: '4px', borderRadius: '50%', backgroundColor: 'text.main' }} />
                  <ArgonTypography variant="button" fontWeight="regular" color="text">
                    {totalStaff.toLocaleString()} staff
                  </ArgonTypography>
                  <ArgonBox sx={{ width: '4px', height: '4px', borderRadius: '50%', backgroundColor: 'text.main' }} />
                  <ArgonTypography variant="button" fontWeight="regular" color="text">
                    {formatCurrency(totalExpectedPaye)} PAYE
                  </ArgonTypography>
                  <ArgonBox sx={{ width: '4px', height: '4px', borderRadius: '50%', backgroundColor: 'text.main' }} />
                  <ArgonTypography variant="button" fontWeight="regular" color="text">
                    {paidCount} paid
                  </ArgonTypography>
                </ArgonBox>
              </Grid>
              <Grid item xs={12} md={6}>
                <ArgonBox display="flex" justifyContent="flex-end">
                  <TextField
                    placeholder="Search hospitals..."
                    variant="outlined"
                    size="small"
                    value={searchQuery}
                    onChange={handleSearchChange}
                    sx={{ 
                      minWidth: '250px',
                      '& .MuiOutlinedInput-root': {
                        borderRadius: '10px',
                      }
                    }}
                    InputProps={{
                      startAdornment: (
                        <InputAdornment position="start">
                          <SearchIcon />
                        </InputAdornment>
                      ),
                    }}
                  />
                </ArgonBox>
              </Grid>
            </Grid>

            {/* Tabs Section */}
            <ArgonBox mb={3}>
              <Tabs 
                value={tabValue} 
                onChange={handleTabChange} 
                aria-label="hospital type tabs"
                sx={{ 
                  borderBottom: 1, 
                  borderColor: 'divider',
                  '& .MuiTab-root': {
                    minWidth: '200px',
                    fontWeight: 'medium',
                  },
                  '& .Mui-selected': {
                    fontWeight: 'bold',
                  }
                }}
              >
                <Tab 
                  label={`Public Hospitals (${rows.filter(row => row.type?.props?.type === "public").length})`}
                  sx={{ textTransform: 'none' }}
                />
                <Tab 
                  label={`Private Hospitals (${rows.filter(row => row.type?.props?.type === "private").length})`}
                  sx={{ textTransform: 'none' }}
                />
              </Tabs>
            </ArgonBox>

            {/* Filters Section */}
            <ArgonBox 
              display="flex" 
              gap={2} 
              mb={3} 
              flexWrap="wrap"
              sx={{
                '& .MuiButton-root': {
                  textTransform: 'none',
                  borderRadius: '20px',
                  px: 2,
                  py: 0.5,
                  minHeight: '36px',
                  border: '1px solid',
                  borderColor: 'grey.300',
                  backgroundColor: 'white',
                  color: 'text.primary',
                  '&:hover': {
                    backgroundColor: 'grey.100',
                    borderColor: 'grey.400',
                  },
                },
                '& .MuiButton-startIcon': {
                  marginRight: 1,
                },
              }}
            >
              <Button
                variant="outlined"
                startIcon={<Icon>verified</Icon>}
                onClick={(event) => {
                  const anchorEl = event.currentTarget;
                  setLicenseMenuAnchor(anchorEl);
                }}
              >
                {licenseFilter === 'all' ? 'License Status' : 
                 licenseFilter === 'renewed' ? 'Renewed' : 'Expired'}
              </Button>
              <Menu
                anchorEl={licenseMenuAnchor}
                open={Boolean(licenseMenuAnchor)}
                onClose={() => setLicenseMenuAnchor(null)}
              >
                <MenuItem 
                  onClick={() => {
                    setLicenseFilter('all');
                    setLicenseMenuAnchor(null);
                  }}
                  selected={licenseFilter === 'all'}
                >
                  All Licenses
                </MenuItem>
                <MenuItem 
                  onClick={() => {
                    setLicenseFilter('renewed');
                    setLicenseMenuAnchor(null);
                  }}
                  selected={licenseFilter === 'renewed'}
                >
                  Renewed
                </MenuItem>
                <MenuItem 
                  onClick={() => {
                    setLicenseFilter('expired');
                    setLicenseMenuAnchor(null);
                  }}
                  selected={licenseFilter === 'expired'}
                >
                  Expired
                </MenuItem>
              </Menu>

              <Button
                variant="outlined"
                startIcon={<Icon>location_on</Icon>}
                onClick={(event) => {
                  const anchorEl = event.currentTarget;
                  setLgaMenuAnchor(anchorEl);
                }}
              >
                {lgaFilter === 'all' ? 'LGA' : lgaFilter}
              </Button>
              <Menu
                anchorEl={lgaMenuAnchor}
                open={Boolean(lgaMenuAnchor)}
                onClose={() => setLgaMenuAnchor(null)}
              >
                <MenuItem 
                  onClick={() => {
                    setLgaFilter('all');
                    setLgaMenuAnchor(null);
                  }}
                  selected={lgaFilter === 'all'}
                >
                  All LGAs
                </MenuItem>
                {uniqueLgas.map(lga => (
                  <MenuItem 
                    key={lga} 
                    onClick={() => {
                      setLgaFilter(lga);
                      setLgaMenuAnchor(null);
                    }}
                    selected={lgaFilter === lga}
                  >
                    {lga}
                  </MenuItem>
                ))}
              </Menu>

              <Button
                variant="outlined"
                startIcon={<Icon>people</Icon>}
                onClick={(event) => {
                  const anchorEl = event.currentTarget;
                  setStaffMenuAnchor(anchorEl);
                }}
              >
                {staffFilter === 'all' ? 'Staff Size' : 
                 staffFilter === 'small' ? '< 100 staff' :
                 staffFilter === 'medium' ? '100-200 staff' : '> 200 staff'}
              </Button>
              <Menu
                anchorEl={staffMenuAnchor}
                open={Boolean(staffMenuAnchor)}
                onClose={() => setStaffMenuAnchor(null)}
              >
                <MenuItem 
                  onClick={() => {
                    setStaffFilter('all');
                    setStaffMenuAnchor(null);
                  }}
                  selected={staffFilter === 'all'}
                >
                  All Sizes
                </MenuItem>
                <MenuItem 
                  onClick={() => {
                    setStaffFilter('small');
                    setStaffMenuAnchor(null);
                  }}
                  selected={staffFilter === 'small'}
                >
                  &lt; 100 staff
                </MenuItem>
                <MenuItem 
                  onClick={() => {
                    setStaffFilter('medium');
                    setStaffMenuAnchor(null);
                  }}
                  selected={staffFilter === 'medium'}
                >
                  100-200 staff
                </MenuItem>
                <MenuItem 
                  onClick={() => {
                    setStaffFilter('large');
                    setStaffMenuAnchor(null);
                  }}
                  selected={staffFilter === 'large'}
                >
                  &gt; 200 staff
                </MenuItem>
              </Menu>

              <Button
                variant="outlined"
                startIcon={<Icon>payment</Icon>}
                onClick={(event) => {
                  const anchorEl = event.currentTarget;
                  setPayeMenuAnchor(anchorEl);
                }}
              >
                {payeFilter === 'all' ? 'PAYE Status' : 
                 payeFilter === 'paid' ? 'Paid' : 'Unpaid'}
              </Button>
              <Menu
                anchorEl={payeMenuAnchor}
                open={Boolean(payeMenuAnchor)}
                onClose={() => setPayeMenuAnchor(null)}
              >
                <MenuItem 
                  onClick={() => {
                    setPayeFilter('all');
                    setPayeMenuAnchor(null);
                  }}
                  selected={payeFilter === 'all'}
                >
                  All Status
                </MenuItem>
                <MenuItem 
                  onClick={() => {
                    setPayeFilter('paid');
                    setPayeMenuAnchor(null);
                  }}
                  selected={payeFilter === 'paid'}
                >
                  Paid
                </MenuItem>
                <MenuItem 
                  onClick={() => {
                    setPayeFilter('unpaid');
                    setPayeMenuAnchor(null);
                  }}
                  selected={payeFilter === 'unpaid'}
                >
                  Unpaid
                </MenuItem>
              </Menu>
            </ArgonBox>

            {/* Table Section */}
            <ArgonBox>
              <ArgonBox 
                display="flex" 
                gap={1} 
                mb={2}
                sx={{
                  borderBottom: '1px solid',
                  borderColor: 'grey.300',
                  pb: 1
                }}
              >
                {columns.map(column => (
                  <Tooltip 
                    key={column.name} 
                    title={`Sort by ${column.name}${sortConfig.key === column.name ? 
                      ` (${sortConfig.direction === 'asc' ? 'ascending' : 'descending'})` : ''}`}
                  >
                    <IconButton
                      size="small"
                      onClick={() => handleSort(column.name)}
                      sx={{ 
                        opacity: sortConfig.key === column.name ? 1 : 0.5,
                        transform: sortConfig.key === column.name && sortConfig.direction === 'desc' ? 'rotate(180deg)' : 'none',
                        transition: 'all 0.2s ease-in-out',
                        '&:hover': {
                          opacity: 0.8,
                          backgroundColor: 'grey.100'
                        }
                      }}
                    >
                      <SortIcon fontSize="small" />
                    </IconButton>
                  </Tooltip>
                ))}
              </ArgonBox>

              <Table 
                columns={tableData.columns} 
                rows={tableData.rows}
                noDataText="No hospitals match the current filters"
              />
            </ArgonBox>
          </ArgonBox>
        </Card>
      </ArgonBox>
      <Footer />
    </DashboardLayout>
  );
}

export default Hospitals;
